const uuid = require('uuid').v4;
/**
 * Class to create a new user
 */
class User {
  /**
   * New user details
   * @param {Object} [regInfo] - User registration information
   * @param {String} [regInfo.id=uuid()] - Autogenerated user uuid
   * @param {String} [regInfo.name=TEST_NAME] - Provided user name
   * @param {String} [regInfo.login=TEST_LOGIN] - Provided user login
   * @param {String} [regInfo.password=TEST_PWD] - Provided user password
   */
  constructor({
    id = uuid(),
    name = 'TEST_NAME',
    login = 'TEST_LOGIN',
    password = 'TEST_PWD',
  } = {}) {
    /** @property {String} id - autogenerated user id */
    this.id = id;
    /** @property {String} name - user name */
    this.name = name;
    /** @property {String} login - user login */
    this.login = login;
    /** @property {String} id - user password */
    this.password = password;
  }

  /**
   * @property {Function} toResponse Removes password from user info and send back to caller
   * @param {User} user a User class instance
   * @returns {Object} User class object with trimmed password
   */
  static toResponse(user) {
    const { id, name, login } = user;
    return { id, name, login };
  }
}
module.exports = User;
